richText.Text = "Sample";

IFont font = style.Font;

font.Color = Color.Red;

richText.SetFont(0, 5, font);


//Check HasRichText

Console.Write(worksheet["C2"].HasRichText);


//Save to file

workbook.SaveToFile("HasRichText.xlsx");

