chart.DataRange = worksheet.Range["A1:F2"];


//Set chart category axis

IChartCategoryAxis categoryAxis = chart.PrimaryCategoryAxis;


//Set tick mark spacing

categoryAxis.TickMarkSpacing = 2;


//Save to file

workbook.SaveToFile("Chart.xlsx");

