Worksheet worksheet = workbook.Worksheets[0];


//Auto-fit rows

worksheet.Range["A2"].Text = "Fit the content to row";

worksheet.Range["A2"].IsWrapText = true;

worksheet.Range["A2"].AutoFitRows();


//Save to file

workbook.SaveToFile("AutoFitRows.xlsx");

