worksheet["F3:G3"].Merge();


//Get merged ranges

IXLSRange[] mergedRanges = worksheet.MergedCells;


//Get merged range count . Output will be 2

Console.Write(mergedRanges.Length);


//Save to file

workbook.SaveToFile("MergedCells.xlsx");

